INSERT INTO `posts` (`id`, `category_id`, `author_id`, `title`, `excerpt`, `img_thumnail`, `img_cover`, `content`, `is_trending`, `view_count`, `status`, `created_at`, `updated_at`) VALUES
(1, 1, 1, 'Giá vàng quay đầu giảm hơn nửa triệu đồng', 'Mỗi lượng vàng miếng sáng nay hạ 600.000 đồng, lùi về 81 triệu, nhẫn trơn cũng giảm hơn nửa triệu đồng sau khi phá đỉnh vào hôm qua.\r\n', 'uploads/posts/1712478625-tin1.jpg', 'uploads/posts/1712400550-tin1.jpg', '<div class=\"social_pin\">&nbsp;</div>\r\n<div class=\"sidebar-1\">\r\n<div class=\"header-content width_common\">&nbsp;</div>\r\n<h1 class=\"title-detail\">Gi&aacute; v&agrave;ng quay đầu giảm hơn nửa triệu đồng</h1>\r\n<p class=\"description\">Mỗi lượng v&agrave;ng miếng s&aacute;ng nay hạ 600.000 đồng, l&ugrave;i về 81 triệu, nhẫn trơn cũng giảm hơn nửa triệu đồng sau khi ph&aacute; đỉnh v&agrave;o h&ocirc;m qua.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">S&aacute;ng 5/4, C&ocirc;ng ty V&agrave;ng bạc đ&aacute; qu&yacute; S&agrave;i G&ograve;n (SJC) yết&nbsp;<a href=\"https://vnexpress.net/chu-de/gia-vang-1403\" rel=\"dofollow\" data-itm-source=\"#vn_source=Detail-KinhDoanh_HangHoa-4730881&amp;vn_campaign=Box-InternalLink&amp;vn_medium=Link-GiaVang&amp;vn_term=Desktop&amp;vn_thumb=0\" data-itm-added=\"1\">gi&aacute; v&agrave;ng</a>&nbsp;miếng tại 79,1 - 81,1 triệu đồng, giảm 600.000 đồng so với ng&agrave;y h&ocirc;m qua. Tập đo&agrave;n V&agrave;ng bạc đ&aacute; qu&yacute; DOJI cũng hạ gi&aacute; v&agrave;ng miếng xuống 78,4 - 80,7 triệu đồng.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture data-inimage=\"done\"><source srcset=\"https://i1-kinhdoanh.vnecdn.net/2024/04/05/L1034418-JPG-8014-1712294000.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=3A07BFzsz3ybdqkKwf9eiw 1x, https://i1-kinhdoanh.vnecdn.net/2024/04/05/L1034418-JPG-8014-1712294000.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=h_Gkzcwn_KT296sCtg1Pdg 1.5x, https://i1-kinhdoanh.vnecdn.net/2024/04/05/L1034418-JPG-8014-1712294000.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=Nbn9xnXSu1fs0f02WNrQmg 2x\" data-srcset=\"https://i1-kinhdoanh.vnecdn.net/2024/04/05/L1034418-JPG-8014-1712294000.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=3A07BFzsz3ybdqkKwf9eiw 1x, https://i1-kinhdoanh.vnecdn.net/2024/04/05/L1034418-JPG-8014-1712294000.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=h_Gkzcwn_KT296sCtg1Pdg 1.5x, https://i1-kinhdoanh.vnecdn.net/2024/04/05/L1034418-JPG-8014-1712294000.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=Nbn9xnXSu1fs0f02WNrQmg 2x\"><img class=\"lazy lazied\" src=\"https://i1-kinhdoanh.vnecdn.net/2024/04/05/L1034418-JPG-8014-1712294000.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=3A07BFzsz3ybdqkKwf9eiw\" alt=\"Giao dịch v&agrave;ng tại một tiệm kim ho&agrave;n ở H&agrave; Nội. Ảnh: Ngọc Th&agrave;nh\" loading=\"lazy\" data-src=\"https://i1-kinhdoanh.vnecdn.net/2024/04/05/L1034418-JPG-8014-1712294000.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=3A07BFzsz3ybdqkKwf9eiw\" data-ll-status=\"loaded\"></picture>\r\n<div class=\"embed-container-ads\">&nbsp;</div>\r\n</div>\r\n<figcaption>\r\n<p class=\"Image\">Giao dịch v&agrave;ng tại một tiệm kim ho&agrave;n ở H&agrave; Nội. Ảnh:<em>&nbsp;Ngọc Th&agrave;nh</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Sau khi ph&aacute; đỉnh 72 triệu đồng v&agrave;o h&ocirc;m qua, v&agrave;ng nhẫn trơn 24K s&aacute;ng nay cũng quay đầu giảm mạnh. Mỗi lượng nhẫn trơn 24K tại SJC hạ 500.000 đồng, xuống 70,5 - 71,75 triệu đồng. Bảo T&iacute;n Minh Ch&acirc;u cũng điều chỉnh gi&aacute; v&agrave;ng nhẫn xuống 70,8 - 72 triệu đồng một lượng.</p>\r\n<p class=\"Normal\">Gi&aacute; v&agrave;ng trong nước đi xuống theo diễn biến thế giới. Sau khi lập đỉnh tr&ecirc;n 2.300 USD h&ocirc;m qua, mỗi ounce v&agrave;ng giao ngay hiện giảm 15 USD xuống 2.276 USD. Quy đổi theo tỷ gi&aacute; Vietcombank, mỗi lượng v&agrave;ng quốc tế tương đương 68,96 triệu đồng (chưa thuế, ph&iacute;). Theo đ&oacute;, ch&ecirc;nh lệch giữa v&agrave;ng miếng v&agrave; thế giới hiện quanh 12 triệu đồng một lượng c&ograve;n v&agrave;ng nhẫn v&ecirc;nh khoảng 2,8-3 triệu đồng.</p>\r\n</article>\r\n</div>', 1, 360, 'published', '2024-04-06 17:49:10', '2024-04-07 08:30:25'),
(2, 1, 2, ' Kinh doanhQuốc tế: Áp lực sản xuất dư thừa ở Trung Quốc', 'Biết thị trường trong nước dư thừa và gặp rủi ro từ các hạn chế thương mại của phương Tây nhưng Wang Rongshuo vẫn quyết tâm mở rộng kinh doanh.', 'uploads/posts/1712478605-tin2.jpg', 'uploads/posts/1712401097-tin2.jpg', '<div class=\"social_pin\">&nbsp;</div>\r\n<div class=\"sidebar-1\">\r\n<h1 class=\"title-detail\">&Aacute;p lực sản xuất dư thừa ở Trung Quốc</h1>\r\n<p class=\"description\">Biết thị trường trong nước dư thừa v&agrave; gặp rủi ro từ c&aacute;c hạn chế thương mại của phương T&acirc;y nhưng Wang Rongshuo vẫn quyết t&acirc;m mở rộng kinh doanh.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Wang l&agrave; nh&agrave; s&aacute;ng lập c&ocirc;ng ty C&ocirc;ng nghệ xanh Yangshuo (Quảng Đ&ocirc;ng). Từ v&agrave;i chục nh&acirc;n vi&ecirc;n, quy m&ocirc; nh&acirc;n sự c&ocirc;ng ty Wang đ&atilde; tăng l&ecirc;n h&agrave;ng trăm người ở trụ sở ch&iacute;nh. Đ&oacute; l&agrave; chưa t&iacute;nh đến h&agrave;ng ngh&igrave;n c&ocirc;ng nh&acirc;n m&agrave; Wang đ&atilde; k&yacute; hợp đồng tại c&aacute;c c&ocirc;ng trường x&acirc;y dựng khắp đất nước.</p>\r\n<p class=\"Normal\">Sự tự tin n&agrave;y đến từ việc Trung Quốc ưu ti&ecirc;n tăng trưởng trong lĩnh vực năng lượng mới, gồm xe điện, pin lithium-ion v&agrave; tấm pin mặt trời &ndash; được gọi l&agrave; \"ba lĩnh vực mới\". Trong bối cảnh hậu Covid-19 v&agrave; c&aacute;c tranh chấp kinh tế song phương t&aacute;c động lớn đến tăng trưởng, Trung Quốc cho rằng việc nu&ocirc;i dưỡng \"lực lượng sản xuất mới\" l&agrave; rất quan trọng.</p>\r\n<p class=\"Normal\">\"Đột ph&aacute; c&ocirc;ng nghệ trong lĩnh vực năng lượng mới quan trọng với an ninh quốc gia của Trung Quốc\" ,Wang đồng t&igrave;nh.</p>\r\n<p class=\"Normal\">Năm 2020, c&ocirc;ng ty Wang lắp đặt tổng c&ocirc;ng suất 1,1 gigawatt, tương đương với 5,2 tỷ kWh điện t&aacute;i tạo. Kể từ đ&oacute;, con số n&agrave;y đ&atilde; tăng l&ecirc;n 3,6 gigawatt, tương đương 102 tỷ kWh. Tr&ecirc;n cả nước, Trung Quốc bổ sung gần 217 gigawatt c&ocirc;ng suất quang điện v&agrave;o 2023, gấp gần 2,5 lần so với năm 2022 v&agrave; chiếm hơn một nửa c&ocirc;ng suất quang điện mới thế giới. Những con số n&agrave;y vượt xa nhu cầu trong nước.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture data-inimage=\"done\"><source srcset=\"https://i1-kinhdoanh.vnecdn.net/2024/04/03/2009-12-08T120000Z-1857739952-7795-2990-1712147968.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=uSp_Z7T_5vBlucMpL7IhcQ 1x, https://i1-kinhdoanh.vnecdn.net/2024/04/03/2009-12-08T120000Z-1857739952-7795-2990-1712147968.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Ml8sR4_nEWsr9A7v5bDQIw 1.5x, https://i1-kinhdoanh.vnecdn.net/2024/04/03/2009-12-08T120000Z-1857739952-7795-2990-1712147968.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=DTUaGdmTdW0r2EJD_JEjeQ 2x\" data-srcset=\"https://i1-kinhdoanh.vnecdn.net/2024/04/03/2009-12-08T120000Z-1857739952-7795-2990-1712147968.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=uSp_Z7T_5vBlucMpL7IhcQ 1x, https://i1-kinhdoanh.vnecdn.net/2024/04/03/2009-12-08T120000Z-1857739952-7795-2990-1712147968.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Ml8sR4_nEWsr9A7v5bDQIw 1.5x, https://i1-kinhdoanh.vnecdn.net/2024/04/03/2009-12-08T120000Z-1857739952-7795-2990-1712147968.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=DTUaGdmTdW0r2EJD_JEjeQ 2x\"><img class=\"lazy lazied\" src=\"https://i1-kinhdoanh.vnecdn.net/2024/04/03/2009-12-08T120000Z-1857739952-7795-2990-1712147968.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=uSp_Z7T_5vBlucMpL7IhcQ\" alt=\"Nh&acirc;n vi&ecirc;n kiểm tra c&aacute;c tấm pin mặt trời tại nh&agrave; m&aacute;y ở H&agrave;ng Ch&acirc;u, Chiết Giang. Ảnh: Reuters\" loading=\"lazy\" data-src=\"https://i1-kinhdoanh.vnecdn.net/2024/04/03/2009-12-08T120000Z-1857739952-7795-2990-1712147968.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=uSp_Z7T_5vBlucMpL7IhcQ\" data-ll-status=\"loaded\"></picture>\r\n<div class=\"embed-container-ads\">&nbsp;</div>\r\n</div>\r\n<figcaption>\r\n<p class=\"Image\">Nh&acirc;n vi&ecirc;n kiểm tra c&aacute;c tấm pin mặt trời tại nh&agrave; m&aacute;y ở H&agrave;ng Ch&acirc;u, Chiết Giang. Ảnh:&nbsp;<em>Reuters</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Đ&atilde; c&oacute; lo ngại \"ba lĩnh vực mới\" tăng trưởng kh&ocirc;ng bền vững. Theo c&ocirc;ng ty nghi&ecirc;n cứu thị trường Rhodium Group (Mỹ), tỷ lệ sử dụng c&ocirc;ng suất tổng hợp của nền kinh tế Trung Quốc năm ngo&aacute;i đ&atilde; giảm xuống dưới 75%, lần đầu ti&ecirc;n kể từ 2016.</p>\r\n<p class=\"Normal\">Giới chức gần đ&acirc;y thừa nhận \"dư thừa c&ocirc;ng suất ở một số ng&agrave;nh\" l&agrave; th&aacute;ch thức kinh tế lớn cần giải quyết năm nay. Arnold Dou, một kỹ sư kỳ cựu ng&agrave;nh năng lượng mới dự b&aacute;o nhiều doanh nghiệp sẽ sụp đổ trong 1-2 năm tới. \"Vốn, c&ocirc;ng nghệ v&agrave; nh&acirc;n t&agrave;i đang đổ v&agrave;o c&aacute;c ng&agrave;nh n&agrave;y. Trong khi nhận được sự th&uacute;c đẩy lớn v&agrave; đạt được sự tiến bộ nhanh ch&oacute;ng, sự cạnh tranh đặc biệt khốc liệt cũng đang diễn ra\", &ocirc;ng n&oacute;i.</p>\r\n<div id=\"sis_outstream__c\" data-set=\"dfp\"></div>\r\n<p class=\"Normal\">Trước &aacute;p lực kh&oacute; ti&ecirc;u thụ hết trong nước, c&aacute;c c&ocirc;ng ty như Wang t&igrave;m ra quốc tế. \"Nhiều c&ocirc;ng ty sẽ tiếp tục đầu tư v&agrave; kh&aacute;m ph&aacute; th&ecirc;m thị trường. Nếu kh&ocirc;ng, họ sẽ kh&oacute; tồn tại nếu chỉ dựa v&agrave;o thị trường nội địa\", Wang giải th&iacute;ch. &Ocirc;ng c&oacute; kế hoạch đến Mexico năm nay.</p>\r\n<p class=\"Normal\">Khi giải tỏa &aacute;p lực ti&ecirc;u thụ ra nước ngo&agrave;i,&nbsp;<strong>Bắc Kinh phải đối mặt với chỉ tr&iacute;ch từ Mỹ v&agrave; Li&ecirc;n minh ch&acirc;u &Acirc;u, lo ngại rằng c&aacute;c c&ocirc;ng ty Trung Quốc đang xả h&agrave;ng gi&aacute; rẻ</strong>. \"Điều n&agrave;y đặt Trung Quốc, EU v&agrave; Mỹ v&agrave;o t&igrave;nh thế đối đầu thương mại nguy hiểm năm 2024, với khả năng cao xảy ra c&aacute;c trường hợp h&agrave;nh động ph&ograve;ng thủ thương mại\", c&aacute;c nh&agrave; ph&acirc;n t&iacute;ch của Rhodium cho biết.</p>\r\n<p class=\"Normal\">Cuối th&aacute;ng 2, Tổng thống Mỹ Joe Biden cho biết đang thực hiện \"h&agrave;nh động chưa từng c&oacute;\" trước diễn biến n&agrave;y. \"C&aacute;c ch&iacute;nh s&aacute;ch của Trung Quốc c&oacute; thể khiến xe điện của họ tr&agrave;n ngập thị trường ch&uacute;ng ta, g&acirc;y rủi ro an ninh quốc gia\", &ocirc;ng n&oacute;i.</p>\r\n<p class=\"Normal\">Nh&agrave; ph&acirc;n t&iacute;ch He Weiwen cho biết thuế quan &aacute;p l&ecirc;n c&aacute;c sản phẩm của Trung Quốc kể từ khi chiến tranh thương mại Mỹ - Trung bắt đầu v&agrave;o năm 2018 c&oacute; t&aacute;c động hạn chế đến h&agrave;ng xuất khẩu của Trung Quốc. Nhưng c&aacute;c động th&aacute;i ngăn chặn c&ocirc;ng nghệ dưới thời ch&iacute;nh quyền Biden đ&atilde; gi&aacute;ng tiếp một đ&ograve;n mạnh hơn nữa.</p>\r\n<p class=\"Normal\">Cựu tổng thống Mỹ Donald Trump cũng cho biết trong một b&agrave;i ph&aacute;t biểu tranh cử hồi đầu th&aacute;ng rằng &ocirc;ng sẽ &aacute;p thuế 100% với &ocirc;t&ocirc; Trung Quốc sản xuất tại Mexico v&agrave; n&acirc;ng nhiều mức thuế l&ecirc;n tới 60% đối với c&aacute;c sản phẩm của Trung Quốc nếu &ocirc;ng t&aacute;i đắc cử. \"Trung Quốc vẫn cần &aacute;p dụng chiến thuật ph&ograve;ng ngừa th&aacute;ch thức nếu Trump quay lại nắm quyền\", &ocirc;ng He Weiwen n&oacute;i.</p>\r\n<p class=\"Normal\">Trong động th&aacute;i mới nhất, Bộ trưởng T&agrave;i ch&iacute;nh Mỹ Janet Yellen đ&atilde; đ&aacute;p m&aacute;y bay đến Trung Quốc h&ocirc;m 3/4. Chuyến thăm thứ hai của b&agrave; Yellen tới Trung Quốc diễn ra đến ng&agrave;y 9/4. B&agrave; sẽ gh&eacute; nh&agrave; m&aacute;y ph&iacute;a nam v&agrave; trung t&acirc;m xuất khẩu Quảng Ch&acirc;u trước khi đến Bắc Kinh.</p>\r\n<p class=\"Normal\">Một quan chức Bộ T&agrave;i ch&iacute;nh Mỹ n&oacute;i rằng trong chuyến đi n&agrave;y, b&agrave; Yellen sẽ \"l&agrave;m r&otilde; những hậu quả kinh tế to&agrave;n cầu\" do t&igrave;nh trạng dư thừa năng lực c&ocirc;ng nghiệp của Trung Quốc đang g&acirc;y thiệt hại cho c&aacute;c nh&agrave; sản xuất ở Mỹ v&agrave; c&aacute;c c&ocirc;ng ty tr&ecirc;n khắp thế giới.</p>\r\n<p class=\"Normal\">Tuần trước, Bộ trưởng T&agrave;i ch&iacute;nh Mỹ n&oacute;i sự hỗ trợ của ch&iacute;nh phủ Trung Quốc đ&atilde; dẫn đến \"đầu tư qu&aacute; mức đ&aacute;ng kể\" v&agrave;o th&eacute;p, nh&ocirc;m v&agrave; gần đ&acirc;y l&agrave; năng lượng mặt trời, xe điện v&agrave; pin lithium-ion. Theo b&agrave;, điều n&agrave;y đ&atilde; b&oacute;p m&eacute;o gi&aacute; cả v&agrave; m&ocirc; h&igrave;nh sản xuất, g&acirc;y tổn hại cho người lao động ở Mỹ, EU v&agrave; c&aacute;c nền kinh tế kh&aacute;c.</p>\r\n<p class=\"Normal\">Ph&iacute;a EU đang điều tra liệu ng&agrave;nh c&ocirc;ng nghiệp xe điện Trung Quốc c&oacute; được hưởng lợi từ c&aacute;c khoản trợ cấp kh&ocirc;ng c&ocirc;ng bằng hay kh&ocirc;ng. Margrethe Vestager, Ủy vi&ecirc;n cạnh tranh của EU, cho biết khối n&agrave;y \"ho&agrave;n to&agrave;n sẵn s&agrave;ng\" sử dụng c&ocirc;ng cụ thương mại để đối ph&oacute; với cạnh tranh kh&ocirc;ng l&agrave;nh mạnh.</p>\r\n<p class=\"Normal\">Rhodium cảnh b&aacute;o mất c&acirc;n bằng trong sản xuất của Trung Quốc cũng c&oacute; thể dẫn đến phản ứng từ nhiều quốc gia hơn như Mexico v&agrave; Brazil nếu t&igrave;nh trạng n&agrave;y tiếp diễn. Tuy nhi&ecirc;n, Zha Daojiong, Gi&aacute;o sư tại Trường Nghi&ecirc;n cứu Quốc tế tại Đại học Bắc Kinh n&oacute;i \"sẽ l&agrave; kh&ocirc;ng kh&ocirc;n ngoan nếu Trung Quốc bắt chước ch&iacute;nh s&aacute;ch của Mỹ hoặc EU với nước n&agrave;y\", tức ăn miếng trả miếng.</p>\r\n<p class=\"Normal\">Một gi&aacute;m đốc lĩnh vực b&aacute;n dẫn tin Trung Quốc c&oacute; thể chống lại &aacute;p lực b&ecirc;n ngo&agrave;i hiệu quả hơn nếu c&oacute; thể tăng tỷ lệ sử dụng năng lượng t&aacute;i tạo trong tổng mức ti&ecirc;u thụ năng lượng. \"Đối với ng&agrave;nh năng lượng mới của Trung Quốc, điểm cất c&aacute;nh sẽ l&agrave; khi tất cả mắt x&iacute;ch trong chuỗi c&ocirc;ng nghiệp đều đủ rẻ, khi đ&oacute; Trung Quốc sẽ thiết lập được lợi thế tuyệt đối\", người n&agrave;y n&oacute;i.</p>\r\n<p class=\"Normal\">C&ocirc;ng ty cung cấp dữ liệu v&agrave; ph&acirc;n t&iacute;ch về chuyển đổi năng lượng to&agrave;n cầu Wood Mackenzie cho biết chi ph&iacute; sản xuất năng lượng mặt trời của Trung Quốc đ&atilde; giảm mạnh 42% v&agrave;o năm ngo&aacute;i, lớn hơn nhiều Ấn Độ, ch&acirc;u &Acirc;u v&agrave; Mỹ. \"Nếu việc triển khai năng lượng mới của Trung Quốc c&oacute; thể đ&aacute;p ứng nhu cầu quốc gia trong 10 đến 15 năm tới, cần g&igrave; lo lắng &aacute;p lực b&ecirc;n ngo&agrave;i\", Wang n&oacute;i.</p>\r\n</article>\r\n</div>', 0, 96, 'published', '2024-04-06 17:58:17', '2024-04-07 08:30:05'),
(3, 2, 2, 'Phong cách nữ đại gia Mỹ gốc Việt', 'Đại gia Mỹ gốc Việt Mimi Morris \"biến hóa\" phong cách liên tục với trang phục hàng hiệu xa xỉ.', 'uploads/posts/1712478588-tin3.jpg', 'uploads/posts/1712402322-tin3.jpg', '<p>&nbsp;</p>\r\n<div class=\"width-detail-photo\">\r\n<p class=\"description\"><span style=\"background-color: rgb(241, 196, 15);\"><strong>Đại gia Mỹ gốc Việt Mimi Morris \"biến h&oacute;a\" phong c&aacute;ch li&ecirc;n tục với trang phục h&agrave;ng hiệu xa xỉ.</strong></span></p>\r\n<div class=\"width_common social-com\">&nbsp;</div>\r\n</div>\r\n<article id=\"lightgallery\" class=\"fck_detail\">\r\n<div class=\"item_slide_show clearfix\">\r\n<div id=\"slide_show_33391151\" class=\"block_thumb_slide_show\" data-thumbnail-src=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-13-1712375035.jpg?w=220&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=nYaFm2rrKUCfWktIw7hxJQ\" data-src=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-13-1712375035.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=4pusKxAIbazPJuC6QkviBA\" data-size-image=\"true\">\r\n<div class=\"block_thumb_picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-13-1712375035.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=fTk8WJ4TAPIcCp28t-6UVA 1x, https://i1-giaitri.vnecdn.net/2024/04/06/mimi-13-1712375035.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=b2x4pKIKlaGs9mV7CZH8Sw 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-13-1712375035.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=fTk8WJ4TAPIcCp28t-6UVA 1x, https://i1-giaitri.vnecdn.net/2024/04/06/mimi-13-1712375035.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=b2x4pKIKlaGs9mV7CZH8Sw 2x\"><img class=\"lazy lazy-blur lazy-unblur lazied\" src=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-13-1712375035.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=fTk8WJ4TAPIcCp28t-6UVA\" alt=\"\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-13-1712375035.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=fTk8WJ4TAPIcCp28t-6UVA\" data-ll-status=\"loaded\"></picture></div>\r\n</div>\r\n<div class=\"desc_cation\">\r\n<p class=\"Normal\">H&ocirc;m 4/4, Mimi Morris về nước c&ocirc;ng t&aacute;c, thăm người th&acirc;n. C&ocirc; gặp gỡ doanh nh&acirc;n Phạm Huy Cận, bạn đồng h&agrave;nh của nh&agrave; thiết kế Đỗ Mạnh Cường, ở TP HCM. Người đẹp diện đầm trắng, d&eacute;p b&aacute;nh m&igrave; Chanel, mang t&uacute;i Kelly Hermes size mini, k&iacute;nh Dior, đeo hai nhẫn kim cương, mỗi chiếc 17 carat, đồng hồ Patek Philippe v&agrave; v&agrave;i chiếc v&ograve;ng tay Cartier.</p>\r\n</div>\r\n</div>\r\n<div class=\"item_slide_show clearfix\">\r\n<div id=\"slide_show_33391152\" class=\"block_thumb_slide_show\" data-thumbnail-src=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-11-1712374736.jpg?w=220&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=8PyALt-Tk8560hUK4Cfrxw\" data-src=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-11-1712374736.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=yHWe1V2wbfz6pseicI-R5A\" data-size-image=\"true\">\r\n<div class=\"block_thumb_picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-11-1712374736.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=whnE5SmqIoqvja_GCF70qQ 1x, https://i1-giaitri.vnecdn.net/2024/04/06/mimi-11-1712374736.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=ebut0tm-_hfAmHXjtDsPIA 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-11-1712374736.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=whnE5SmqIoqvja_GCF70qQ 1x, https://i1-giaitri.vnecdn.net/2024/04/06/mimi-11-1712374736.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=ebut0tm-_hfAmHXjtDsPIA 2x\"><img class=\"lazy lazy-blur lazy-unblur lazied\" src=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-11-1712374736.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=whnE5SmqIoqvja_GCF70qQ\" alt=\"\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-11-1712374736.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=whnE5SmqIoqvja_GCF70qQ\" data-ll-status=\"loaded\"></picture></div>\r\n</div>\r\n<div class=\"desc_cation\">\r\n<p class=\"Normal\">Mimi Morris, 54 tuổi, l&agrave; người Việt, vợ triệu ph&uacute; Mỹ Donald Morris. C&ocirc; sang Mỹ từ năm 29 tuổi, g&acirc;y dựng sự nghiệp kinh doanh bất động sản. Người đẹp l&agrave; một trong những nh&acirc;n vật tham gia series&nbsp;<em><a href=\"https://vnexpress.net/tag/bling-empire-1386096\" rel=\"nofollow\" data-itm-source=\"#vn_source=Detail-GiaiTri_ThoiTrang_SaoDepSaoXau-4731183&amp;vn_campaign=Box-InternalLink&amp;vn_medium=Link-BlingEmpire&amp;vn_term=Desktop&amp;vn_thumb=0\" data-itm-added=\"1\">Bling Empire</a>&nbsp;</em>(Đế chế ph&ocirc; trương) m&ugrave;a hai của Netflix, n&oacute;i về giới si&ecirc;u gi&agrave;u ch&acirc;u &Aacute;.</p>\r\n<p class=\"Normal\">Donald Morris, 75 tuổi, sinh tại Los Angeles, tốt nghiệp Đại học B&aacute;ch khoa California, lấy bằng Thạc sĩ Kỹ thuật X&acirc;y dựng tại Đại học Stanford. Hiện &ocirc;ng l&agrave; gi&aacute;m đốc điều h&agrave;nh của Morris Group International - c&ocirc;ng ty chuy&ecirc;n c&aacute;c sản phẩm ống nước v&agrave; thiết bị ph&ograve;ng ch&aacute;y chữa ch&aacute;y - v&agrave; CEO của c&ocirc;ng ty kỹ thuật Acorn ở Mỹ. Năm 2017, tạp ch&iacute;&nbsp;<em>The Wholesaler</em>&nbsp;chọn &ocirc;ng l&agrave; Nh&acirc;n vật của năm. Theo&nbsp;<em>SCMP</em>, &ocirc;ng đang giữ khối t&agrave;i sản hơn 800 triệu USD.</p>\r\n</div>\r\n</div>\r\n<div class=\"item_slide_show clearfix\">\r\n<div id=\"slide_show_33391031\" class=\"block_thumb_slide_show\" data-thumbnail-src=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-3-1712373022.jpg?w=220&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ka-M8JNVZM3JkPVarshL2A\" data-src=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-3-1712373022.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=jT6dGQ-63cg-OkmDhlR3Bw\" data-size-image=\"true\">\r\n<div class=\"block_thumb_picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-3-1712373022.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2JPkmKSBwE65REqLM5yevg 1x, https://i1-giaitri.vnecdn.net/2024/04/06/mimi-3-1712373022.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=M5TH7vpG2Q99OKTwpWnvsQ 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-3-1712373022.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2JPkmKSBwE65REqLM5yevg 1x, https://i1-giaitri.vnecdn.net/2024/04/06/mimi-3-1712373022.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=M5TH7vpG2Q99OKTwpWnvsQ 2x\"><img class=\"lazy lazy-blur lazy-unblur lazied\" src=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-3-1712373022.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2JPkmKSBwE65REqLM5yevg\" alt=\"\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-3-1712373022.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2JPkmKSBwE65REqLM5yevg\" data-ll-status=\"loaded\"></picture></div>\r\n</div>\r\n<div class=\"desc_cation\">\r\n<p class=\"Normal\">Trong&nbsp;<em>Bling Empire,&nbsp;</em>Mimi cho biết gia đ&igrave;nh sống hạnh ph&uacute;c trong căn biệt thự tại Mỹ. Tr&ecirc;n trang c&aacute; nh&acirc;n, c&ocirc; thường chia sẻ th&uacute; vui mua sắm h&agrave;ng thời trang xa xỉ. Người đẹp mặc đa phong c&aacute;ch với những bộ đầm xếp b&egrave;o cồng kềnh, khi lại thanh lịch với bộ c&aacute;nh tối giản.</p>\r\n</div>\r\n</div>\r\n<div class=\"item_slide_show clearfix\">\r\n<div id=\"slide_show_33391038\" class=\"block_thumb_slide_show\" data-thumbnail-src=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-1712373026.jpg?w=220&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=F1doXOcdY29ON_7pCm6NjQ\" data-src=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-1712373026.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=rF38ngTng0QL8IgskIS0FQ\" data-size-image=\"true\">\r\n<div class=\"block_thumb_picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-1712373026.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YeHVCdbL-deUr9uQ1DVbeA 1x, https://i1-giaitri.vnecdn.net/2024/04/06/mimi-1712373026.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=Yo3OXKRhsMlLpEQnebD5rA 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-1712373026.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YeHVCdbL-deUr9uQ1DVbeA 1x, https://i1-giaitri.vnecdn.net/2024/04/06/mimi-1712373026.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=Yo3OXKRhsMlLpEQnebD5rA 2x\"><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-1712373026.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YeHVCdbL-deUr9uQ1DVbeA\" alt=\"\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-1712373026.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YeHVCdbL-deUr9uQ1DVbeA\" data-ll-status=\"loaded\"></picture></div>\r\n</div>\r\n<div class=\"desc_cation\">\r\n<p class=\"Normal\">Mimi c&ugrave;ng chồng con đến Ninh Thuận dự&nbsp;<a href=\"https://vnexpress.net/san-catwalk-gan-400-m-ben-bo-bien-cua-do-manh-cuong-4481088.html\" rel=\"nofollow\" data-itm-source=\"#vn_source=Detail-GiaiTri_ThoiTrang_SaoDepSaoXau-4731183&amp;vn_campaign=Box-InternalLink&amp;vn_medium=Link-ShowSixdo&amp;vn_term=Desktop&amp;vn_thumb=0\" data-itm-added=\"1\">show Sixdo</a>&nbsp;của&nbsp;<a href=\"https://vnexpress.net/chu-de/nha-thiet-ke-do-manh-cuong-5199\" rel=\"nofollow\" data-itm-source=\"#vn_source=Detail-GiaiTri_ThoiTrang_SaoDepSaoXau-4731183&amp;vn_campaign=Box-InternalLink&amp;vn_medium=Link-DoManhCuong&amp;vn_term=Desktop&amp;vn_thumb=0\" data-itm-added=\"1\">Đỗ Mạnh Cường</a>&nbsp;b&ecirc;n bờ biển vịnh Vĩnh Hy năm 2022. C&ocirc; chọn kiểu đầm bất đối xứng, phối t&uacute;i Lady Dior da c&aacute; sấu. Ngo&agrave;i show n&agrave;y, c&ocirc; nhiều lần xem show kh&aacute;c của nh&agrave; thiết kế gồm&nbsp;<em>Life in color, Twins,</em><em>&nbsp;La Vie En Rose</em>.</p>\r\n</div>\r\n</div>\r\n<div class=\"item_slide_show clearfix\">\r\n<div id=\"slide_show_33391035\" class=\"block_thumb_slide_show\" data-thumbnail-src=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-7-1712373024.jpg?w=220&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YfB496Ee3OdXgNGTjWx-Mw\" data-src=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-7-1712373024.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=tbw4Ys1ziO-0mQGVncTMbw\" data-size-image=\"true\">\r\n<div class=\"block_thumb_picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-7-1712373024.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=n7wzfa_I2dTpH5fL3oZWJQ 1x, https://i1-giaitri.vnecdn.net/2024/04/06/mimi-7-1712373024.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=rrJXQaaZq71aiIFA2DLnnQ 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-7-1712373024.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=n7wzfa_I2dTpH5fL3oZWJQ 1x, https://i1-giaitri.vnecdn.net/2024/04/06/mimi-7-1712373024.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=rrJXQaaZq71aiIFA2DLnnQ 2x\"><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-7-1712373024.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=n7wzfa_I2dTpH5fL3oZWJQ\" alt=\"\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2024/04/06/mimi-7-1712373024.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=n7wzfa_I2dTpH5fL3oZWJQ\" data-ll-status=\"loaded\"></picture></div>\r\n</div>\r\n</div>\r\n</article>', 1, 250, 'published', '2024-04-06 18:18:42', '2024-04-07 08:29:48'),
(4, 2, 2, 'Arsenal phòng ngự ấn tượng thế nào tại Ngoại hạng Anh', 'Theo chuyên gia Ngoại hạng Anh Adrian Clarke, việc pressing đồng bộ với hai trung vệ thi đấu lăn xả giúp Arsenal sở hữu hàng thủ chắc chắn với những thông số tốt nhất giải.', 'uploads/posts/1712478549-tin4.jpg', 'uploads/posts/1712404159-tin4.jpg', '<h1 class=\"title-detail\">Arsenal ph&ograve;ng ngự ấn tượng thế n&agrave;o tại Ngoại hạng Anh</h1>\r\n<p class=\"description\">Theo chuy&ecirc;n gia Ngoại hạng Anh Adrian Clarke, việc pressing đồng bộ với hai trung vệ thi đấu lăn xả gi&uacute;p Arsenal sở hữu h&agrave;ng thủ chắc chắn với những th&ocirc;ng số tốt nhất giải.</p>\r\n<article class=\"fck_detail \">\r\n<div id=\"sis_outstream\"></div>\r\n<div class=\"width_common box-widget-thethao\" data-campaign=\"Box-LichDau_KetQua\">\r\n<div class=\"header-tt flexbox\"><span class=\"title-box-tt\">Lịch đấu - Kết quả Ngoại hạng Anh</span></div>\r\n<div class=\"width_common list-match-date scrollbar-inner ss-container\">\r\n<div class=\"ss-wrapper\">\r\n<div class=\"ss-content\">\r\n<div class=\"width_common row-date-m\">\r\n<div class=\"item-date-m width_common\"><span class=\"date-m\">Thứ bảy, ng&agrave;y 6/4/2024</span>\r\n<div class=\"item-match flexbox\"><span class=\"round\">V&ograve;ng 32</span><a class=\"team-kq flexbox\" href=\"https://vnexpress.net/the-thao/du-lieu-bong-da/tran-dau/1035485/brighton-arsenal\" target=\"_blank\" rel=\"noopener\" data-medium=\"Item-1\" data-itm-source=\"#vn_source=Detail-TheThao_NgoaiHangAnh_PhanTich-4731159&amp;vn_campaign=Box-LichDau_KetQua&amp;vn_medium=Item-1&amp;vn_term=Desktop\" data-itm-added=\"1\">\r\n<div class=\"team-pl flexbox\"><span class=\"name-team\">Brighton</span><span class=\"flag-team\"><img src=\"https://is.vnecdn.net/objects/teams/51.png\" alt=\"\"></span></div>\r\n<span class=\"res-m\">23 : 30</span>\r\n<div class=\"team-pl team-away flexbox\"><span class=\"name-team\">Arsenal</span><span class=\"flag-team\"><img src=\"https://is.vnecdn.net/objects/teams/42.png\" alt=\"\"></span></div>\r\n</a></div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<div class=\"ss-scroll ss-hidden\">&nbsp;</div>\r\n</div>\r\n<div class=\"note-bot flexbox\"><a class=\"btn-chitiet\" href=\"https://vnexpress.net/the-thao/du-lieu-bong-da/giai-dau/premier-league\" target=\"_blank\" rel=\"noopener\" data-medium=\"Item-XemTatCa\" data-itm-source=\"#vn_source=Detail-TheThao_NgoaiHangAnh_PhanTich-4731159&amp;vn_campaign=Box-LichDau_KetQua&amp;vn_medium=Item-XemTatCa&amp;vn_term=Desktop\" data-itm-added=\"1\">Xem tất cả</a></div>\r\n</div>\r\n<p class=\"Normal\">Arsenal bất bại qua 10 trận tại Ngoại hạng Anh từ đầu năm 2024 (thắng ch&iacute;n, ho&agrave; một). H&agrave;ng thủ g&oacute;p c&ocirc;ng v&agrave;o mạch kết quả n&agrave;y với s&aacute;u trận giữ sạch lưới v&agrave; chỉ bốn b&agrave;n thua - th&ocirc;ng số tốt nhất giải. Xếp sau Arsenal l&agrave; hai đối thủ cạnh tranh trực tiếp - Man City (8 b&agrave;n thua) v&agrave; Liverpool (11).</p>\r\n<p class=\"Normal\">Trong năm 2024, Arsenal chỉ để c&aacute;c đối thủ dứt điểm trung b&igrave;nh 1,8 c&uacute; mỗi 90 ph&uacute;t v&agrave; c&oacute; chỉ số b&agrave;n thắng kỳ vọng (xG) l&agrave; 4,45. Đ&acirc;y đều l&agrave; th&ocirc;ng số tốt nhất giải.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-thethao.vnecdn.net/2024/04/06/2024-02-04T172110Z-37621625-UP-1880-1871-1712372720.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=WHVaLD6156pPnNsnO1Auqw 1x, https://i1-thethao.vnecdn.net/2024/04/06/2024-02-04T172110Z-37621625-UP-1880-1871-1712372720.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=q362xD4qSnfD8AUdZjyqFA 1.5x, https://i1-thethao.vnecdn.net/2024/04/06/2024-02-04T172110Z-37621625-UP-1880-1871-1712372720.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=rY85GRjxfTi-yBQV65sPrQ 2x\" data-srcset=\"https://i1-thethao.vnecdn.net/2024/04/06/2024-02-04T172110Z-37621625-UP-1880-1871-1712372720.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=WHVaLD6156pPnNsnO1Auqw 1x, https://i1-thethao.vnecdn.net/2024/04/06/2024-02-04T172110Z-37621625-UP-1880-1871-1712372720.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=q362xD4qSnfD8AUdZjyqFA 1.5x, https://i1-thethao.vnecdn.net/2024/04/06/2024-02-04T172110Z-37621625-UP-1880-1871-1712372720.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=rY85GRjxfTi-yBQV65sPrQ 2x\"><img class=\"lazy lazied\" src=\"https://i1-thethao.vnecdn.net/2024/04/06/2024-02-04T172110Z-37621625-UP-1880-1871-1712372720.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=WHVaLD6156pPnNsnO1Auqw\" alt=\"Trung v&ecirc; Arsenal Saliba (&aacute;o đỏ) ngăn tiền đạo Luis Diaz trong trận thắng Liverpool 3-1 tr&ecirc;n s&acirc;n Emirates ở v&ograve;ng 23 Ngoại hạng Anh ng&agrave;y 4/1. Ảnh: Reuters\" loading=\"lazy\" data-src=\"https://i1-thethao.vnecdn.net/2024/04/06/2024-02-04T172110Z-37621625-UP-1880-1871-1712372720.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=WHVaLD6156pPnNsnO1Auqw\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Trung vệ Arsenal Saliba (&aacute;o đỏ) ngăn tiền đạo Luis Diaz trong trận thắng Liverpool 3-1 tr&ecirc;n s&acirc;n Emirates ở v&ograve;ng 23 Ngoại hạng Anh ng&agrave;y 4/1. Ảnh:&nbsp;<em>Reuters</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Đội b&oacute;ng của Mikel Arteta c&ograve;n dẫn đầu ở hai th&ocirc;ng số ph&ograve;ng ngự kh&aacute;c trong năm 2024, đầu ti&ecirc;n l&agrave; số lần để đối phương dứt điểm (77) v&agrave; số lần đối phương dứt điểm tr&uacute;ng đ&iacute;ch (18 lần).</p>\r\n<p class=\"Normal\">T&iacute;nh cả m&ugrave;a giải, Arsenal chỉ phải đối mặt với 71 c&uacute; dứt điểm tr&uacute;ng đ&iacute;ch, &iacute;t hơn Man City (20 c&uacute;) v&agrave; Liverpool (32 c&uacute;). H&agrave;ng thủ chắc chắn gi&uacute;p thủ m&ocirc;n David Raya dẫn đầu cuộc đua gi&agrave;nh Găng Tay V&agrave;ng với 11 trận giữ sạch lưới, bỏ xa Ederson (Man City), Bernd Leno (Fulham), Andre Onana (Man Utd) v&agrave; Jordan Pickford (Everton) - những đồng nghiệp c&ugrave;ng c&oacute; t&aacute;m trận.</p>\r\n<p class=\"Normal\"><strong>Pressing đồng bộ</strong></p>\r\n<p class=\"Normal\">Bộ đ&ocirc;i trung vệ Gabriel v&agrave; William Saliba c&ugrave;ng bản hợp đồng kỷ lục Declan Rice được ca ngợi tạo ra t&aacute;c động to lớn, nhưng khả năng ph&ograve;ng ngự xuất sắc của Arsenal l&agrave; th&agrave;nh quả từ nỗ lực của tập thể. Đội trưởng Martin Odegaard thường chỉ huy tuyến pressing đầu ti&ecirc;n ngay từ tr&ecirc;n h&agrave;ng c&ocirc;ng, gi&uacute;p Arsenal trở th&agrave;nh tập thể ph&ograve;ng ngự với chất lượng v&agrave; t&iacute;nh tổ chức.</p>\r\n<p class=\"Normal\">Sau trận thua 0-2 ở giai đoạn một, HLV Roberto De Zerbi của Brighton thừa nhận gặp kh&oacute; trước lối chơi v&acirc;y r&aacute;p tầm cao n&agrave;y, nhất l&agrave; khi cố gắng triển khai b&oacute;ng từ s&acirc;n nh&agrave;. H&igrave;nh ảnh b&ecirc;n dưới l&agrave; khoảnh khắc chỉ 19 gi&acirc;y sau tiếng c&ograve;i khai cuộc, khi Arsenal đẩy cao đội h&igrave;nh, sử dụng chiến thuật một k&egrave;m một nhằm buộc Brighton phải mắc lỗi ngay phần s&acirc;n nh&agrave;.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-thethao.vnecdn.net/2024/04/06/Arsenal-pressing-v-Brighton-4768-1712370094.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=tpUr8ZyMOaYcPNPik0_OPg 1x, https://i1-thethao.vnecdn.net/2024/04/06/Arsenal-pressing-v-Brighton-4768-1712370094.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=unkJNz1PKGUEjqWIP21r6A 1.5x, https://i1-thethao.vnecdn.net/2024/04/06/Arsenal-pressing-v-Brighton-4768-1712370094.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=7UUXpYWQkcorpgL0_IrrpA 2x\" data-srcset=\"https://i1-thethao.vnecdn.net/2024/04/06/Arsenal-pressing-v-Brighton-4768-1712370094.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=tpUr8ZyMOaYcPNPik0_OPg 1x, https://i1-thethao.vnecdn.net/2024/04/06/Arsenal-pressing-v-Brighton-4768-1712370094.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=unkJNz1PKGUEjqWIP21r6A 1.5x, https://i1-thethao.vnecdn.net/2024/04/06/Arsenal-pressing-v-Brighton-4768-1712370094.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=7UUXpYWQkcorpgL0_IrrpA 2x\"><img class=\"lazy lazied\" src=\"https://i1-thethao.vnecdn.net/2024/04/06/Arsenal-pressing-v-Brighton-4768-1712370094.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=tpUr8ZyMOaYcPNPik0_OPg\" alt=\"Arsenal v&acirc;y r&aacute;p Brighton ngay tr&ecirc;n phần s&acirc;n đối phương từ đầu trận đấu tại Emirates ng&agrave;y 17/12/2023. Ảnh chụp m&agrave;n h&igrave;nh\" loading=\"lazy\" data-src=\"https://i1-thethao.vnecdn.net/2024/04/06/Arsenal-pressing-v-Brighton-4768-1712370094.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=tpUr8ZyMOaYcPNPik0_OPg\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Arsenal v&acirc;y r&aacute;p Brighton ngay tr&ecirc;n phần s&acirc;n đối phương từ đầu trận đấu tại Emirates ng&agrave;y 17/12/2023.&nbsp;<em>Ảnh chụp m&agrave;n h&igrave;nh</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Với phong c&aacute;ch pressing tầm cao như vậy, Arsenal c&oacute; 192 lần gi&agrave;nh b&oacute;ng ở một phần ba cuối s&acirc;n ph&iacute;a cầu m&ocirc;n đối phương. Th&ocirc;ng số n&agrave;y chỉ k&eacute;m Man City (220) v&agrave; Tottenham (200) tại Ngoại hạng Anh 2023-2024.</p>\r\n<p class=\"Normal\"><strong>Cặp trung vệ ăn &yacute;</strong></p>\r\n<p class=\"Normal\">B&ecirc;n s&acirc;n nh&agrave;, tốc độ, sức mạnh v&agrave; sự ăn &yacute; giữa c&aacute;c hậu vệ gi&uacute;p Arsenal dập tắt h&agrave;ng loạt đợt tấn c&ocirc;ng của đối phương. H&ograve;a hợp với nhau v&agrave; ng&agrave;y c&agrave;ng tự tin, Gabriel - Saliba trở th&agrave;nh cặp trung vệ đ&aacute;ng gờm bậc nhất tại giải. Khi bị đẩy l&ugrave;i về khu vực cấm địa, bộ đ&ocirc;i n&agrave;y thể hiện bản lĩnh v&agrave; lăn xả để chặn những c&uacute; s&uacute;t.</p>\r\n<p class=\"Normal\">Arsenal chặn 32,1% những c&uacute; s&uacute;t của đối phương tại Ngoại hạng Anh m&ugrave;a n&agrave;y, th&ocirc;ng số chỉ k&eacute;m Nottingham Forest với 32,2%. T&iacute;nh ri&ecirc;ng Arsenal, Gabriel dẫn đầu th&ocirc;ng số n&agrave;y với 26 pha cản ph&aacute; c&uacute; s&uacute;t, tiếp theo l&agrave; Ben White (17), Saliba (12) v&agrave; Rice (8).</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-thethao.vnecdn.net/2024/04/06/2023-10-08T172659Z-21401085-UP-5150-8294-1712372720.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2q_e2jkWDbLWUv18iVyN4g 1x, https://i1-thethao.vnecdn.net/2024/04/06/2023-10-08T172659Z-21401085-UP-5150-8294-1712372720.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=U9BB3nfUXLPLv27Cbmgv9A 1.5x, https://i1-thethao.vnecdn.net/2024/04/06/2023-10-08T172659Z-21401085-UP-5150-8294-1712372720.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=E-gsfZWP3FSJ7625DdHCtQ 2x\" data-srcset=\"https://i1-thethao.vnecdn.net/2024/04/06/2023-10-08T172659Z-21401085-UP-5150-8294-1712372720.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2q_e2jkWDbLWUv18iVyN4g 1x, https://i1-thethao.vnecdn.net/2024/04/06/2023-10-08T172659Z-21401085-UP-5150-8294-1712372720.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=U9BB3nfUXLPLv27Cbmgv9A 1.5x, https://i1-thethao.vnecdn.net/2024/04/06/2023-10-08T172659Z-21401085-UP-5150-8294-1712372720.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=E-gsfZWP3FSJ7625DdHCtQ 2x\"><img class=\"lazy lazied\" src=\"https://i1-thethao.vnecdn.net/2024/04/06/2023-10-08T172659Z-21401085-UP-5150-8294-1712372720.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2q_e2jkWDbLWUv18iVyN4g\" alt=\"William Saliba v&agrave; Gabriel mừng sau khi Arsenal thắng Man City 1-0 tại Ngoại hạng Anh ng&agrave;y 8/10/2023. Ảnh: Reuters\" loading=\"lazy\" data-src=\"https://i1-thethao.vnecdn.net/2024/04/06/2023-10-08T172659Z-21401085-UP-5150-8294-1712372720.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2q_e2jkWDbLWUv18iVyN4g\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">William Saliba v&agrave; Gabriel mừng sau khi Arsenal thắng Man City 1-0 tại Ngoại hạng Anh ng&agrave;y 8/10/2023. Ảnh:&nbsp;<em>Reuters</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\"><strong>V&iacute; dụ ho&agrave;n hảo từ trận thắng Brighton</strong></p>\r\n<p class=\"Normal\">Arsenal &aacute;p đảo khi hạ Brighton 2-0 ở v&ograve;ng 17 Ngoại hạng Anh. Tr&ecirc;n s&acirc;n Emirates ng&agrave;y 17/2 đ&oacute;, Arsenal kiểm so&aacute;t b&oacute;ng 58%, hưởng ba phạt g&oacute;c, dứt điểm 15 lần với ba c&uacute; tr&uacute;ng đ&iacute;ch trong 45 ph&uacute;t đầu. Brighton gần như chịu trận, kh&ocirc;ng c&oacute; pha phạt g&oacute;c hay dứt điểm n&agrave;o. Lần đầu Brighton trải qua t&igrave;nh cảnh như vậy dưới thời HLV De Zerbi tại Ngoại hạng Anh. D&ugrave; vậy, Arsenal chỉ giải quyết trận đấu trong hiệp hai với những pha lập c&ocirc;ng của Gabriel Jesus v&agrave; Kai Havertz.</p>\r\n<p class=\"Normal\">Odegaard thể hiện vai tr&ograve; thủ lĩnh khi chạy 12 km, c&oacute; 25 t&igrave;nh huống pressing ở một phần ba cuối s&acirc;n v&agrave; gi&agrave;nh lại b&oacute;ng bảy lần từ những t&igrave;nh huống như vậy - đều l&agrave; th&ocirc;ng số cao nhất trận. Những người xếp sau tiền vệ Na Uy l&agrave; Rice, Jesus, Gabriel Martinelli, Bukayo Saka, Smith Rowe v&agrave; Havertz đều chỉ c&oacute; một lần gi&agrave;nh b&oacute;ng từ những t&igrave;nh huống pressing ở một phần ba cuối s&acirc;n.</p>\r\n<p class=\"Normal\">Đồ họa dưới đ&acirc;y cho thấy tần suất \"Ph&aacute;o thủ\" gi&agrave;nh lại b&oacute;ng b&ecirc;n trong phần s&acirc;n của Brighton. Đo&agrave;n qu&acirc;n dưới trướng De Zerbi kh&ocirc;ng thể triển khai b&oacute;ng, v&agrave; Arsenal chắc chắn sẽ cố gắng l&agrave;m điều tương tự khi l&agrave;m kh&aacute;ch của Brighton tr&ecirc;n s&acirc;n Amex h&ocirc;m nay.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-thethao.vnecdn.net/2024/04/06/arsenal-pressing-v-brighton2-j-5545-7842-1712372720.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7Wflyv9h2Zi3Oic5i-0dDA 1x, https://i1-thethao.vnecdn.net/2024/04/06/arsenal-pressing-v-brighton2-j-5545-7842-1712372720.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=OpWOwv8NpDIt6r22Xhs-jw 1.5x, https://i1-thethao.vnecdn.net/2024/04/06/arsenal-pressing-v-brighton2-j-5545-7842-1712372720.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=f1BeN9QJvZmJdXVFWznXyw 2x\" data-srcset=\"https://i1-thethao.vnecdn.net/2024/04/06/arsenal-pressing-v-brighton2-j-5545-7842-1712372720.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7Wflyv9h2Zi3Oic5i-0dDA 1x, https://i1-thethao.vnecdn.net/2024/04/06/arsenal-pressing-v-brighton2-j-5545-7842-1712372720.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=OpWOwv8NpDIt6r22Xhs-jw 1.5x, https://i1-thethao.vnecdn.net/2024/04/06/arsenal-pressing-v-brighton2-j-5545-7842-1712372720.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=f1BeN9QJvZmJdXVFWznXyw 2x\"><img class=\"lazy lazied\" src=\"https://i1-thethao.vnecdn.net/2024/04/06/arsenal-pressing-v-brighton2-j-5545-7842-1712372720.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7Wflyv9h2Zi3Oic5i-0dDA\" alt=\"Biểu đồ cho thấy tần suất Arsenal gi&agrave;nh lại b&oacute;ng tr&ecirc;n s&acirc;n Brighton ng&agrave;y 17/12/2023. M&agrave;u xanh l&agrave; c&aacute;c pha tranh chấp tay đ&ocirc;i th&agrave;nh c&ocirc;ng, m&agrave;u đỏ l&agrave; những c&uacute; tắc b&oacute;ng, m&agrave;u v&agrave;ng l&agrave; c&aacute;c pha can thiệp. Đồ họa: Premier League\" loading=\"lazy\" data-src=\"https://i1-thethao.vnecdn.net/2024/04/06/arsenal-pressing-v-brighton2-j-5545-7842-1712372720.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7Wflyv9h2Zi3Oic5i-0dDA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Biểu đồ cho thấy tần suất Arsenal gi&agrave;nh lại b&oacute;ng tr&ecirc;n s&acirc;n Brighton ng&agrave;y 17/12/2023. M&agrave;u xanh l&agrave; c&aacute;c pha tranh chấp tay đ&ocirc;i th&agrave;nh c&ocirc;ng, m&agrave;u đỏ l&agrave; những c&uacute; tắc b&oacute;ng, m&agrave;u v&agrave;ng l&agrave; c&aacute;c pha can thiệp. Đồ họa:&nbsp;<em>Premier League</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Arteta cũng chuẩn bị kỹ lưỡng khi xoay tua, cho năm vị tr&iacute; ch&iacute;nh thức nghỉ dưỡng sức trong trận gặp Luton Town tr&ecirc;n s&acirc;n Emirates hồi giữa tuần nhằm giữ ch&acirc;n c&aacute;c trụ cột cho chuyến h&agrave;nh qu&acirc;n tới s&acirc;n của Brighton h&ocirc;m nay.</p>\r\n</article>', 1, 95, 'published', '2024-04-06 18:49:19', '2024-04-07 08:29:21');
INSERT INTO `posts` (`id`, `category_id`, `author_id`, `title`, `excerpt`, `img_thumnail`, `img_cover`, `content`, `is_trending`, `view_count`, `status`, `created_at`, `updated_at`) VALUES
(5, 1, 3, '10 tỷ phú của Hollywood', 'Taylor Swift, Rihanna, Kim Kardashian cùng nhiều diễn viên, đạo diễn góp mặt trong danh sách tỷ phú do Forbes công bố.', 'uploads/posts/1712478523-tin5.jpg', 'uploads/posts/1712405255-tin5.jpg', '<h1 class=\"title-detail\">10 tỷ ph&uacute; của Hollywood</h1>\r\n<div class=\"width-detail-photo\">\r\n<p class=\"description\">Taylor Swift, Rihanna, Kim Kardashian c&ugrave;ng nhiều diễn vi&ecirc;n, đạo diễn g&oacute;p mặt trong danh s&aacute;ch tỷ ph&uacute; do Forbes c&ocirc;ng bố.</p>\r\n<div class=\"width_common social-com\">\r\n<div class=\"myvne_save_for_later border\" title=\"Lưu b&agrave;i viết\" data-token=\"87617e8dae7654bfad35cefeaa190f38\" data-article-id=\"4729991\">&nbsp;</div>\r\n</div>\r\n</div>\r\n<article id=\"lightgallery\" class=\"fck_detail\">\r\n<div class=\"item_slide_show clearfix\">\r\n<div id=\"slide_show_33385990\" class=\"block_thumb_slide_show\" data-thumbnail-src=\"https://i1-giaitri.vnecdn.net/2024/04/03/an11210196star-wars-creator-1712117802-1712119629-1712120703.jpg?w=220&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=nWm1mZm32y6zE4sAgwSdIQ\" data-src=\"https://i1-giaitri.vnecdn.net/2024/04/03/an11210196star-wars-creator-1712117802-1712119629-1712120703.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=k6XbB-borlgABwByZm3Rmg\" data-size-image=\"true\">\r\n<div class=\"block_thumb_picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2024/04/03/an11210196star-wars-creator-1712117802-1712119629-1712120703.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=DanAL0jqckoWbaH0XvOXvw 1x, https://i1-giaitri.vnecdn.net/2024/04/03/an11210196star-wars-creator-1712117802-1712119629-1712120703.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=K4y--aS_t-tHkBPOq78q0Q 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2024/04/03/an11210196star-wars-creator-1712117802-1712119629-1712120703.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=DanAL0jqckoWbaH0XvOXvw 1x, https://i1-giaitri.vnecdn.net/2024/04/03/an11210196star-wars-creator-1712117802-1712119629-1712120703.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=K4y--aS_t-tHkBPOq78q0Q 2x\"><img class=\"lazy lazy-blur lazy-unblur lazied\" src=\"https://i1-giaitri.vnecdn.net/2024/04/03/an11210196star-wars-creator-1712117802-1712119629-1712120703.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=DanAL0jqckoWbaH0XvOXvw\" alt=\"\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2024/04/03/an11210196star-wars-creator-1712117802-1712119629-1712120703.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=DanAL0jqckoWbaH0XvOXvw\" data-ll-status=\"loaded\"></picture></div>\r\n</div>\r\n<div class=\"desc_cation\">\r\n<p class=\"Normal\"><em>Forbes&nbsp;</em>c&ocirc;ng bố bảng xếp hạng 2.781 tỷ ph&uacute; năm 2024, h&ocirc;m 2/4. Đ&acirc;y l&agrave; danh s&aacute;ch người gi&agrave;u c&oacute; nhất thế giới, t&iacute;nh theo gi&aacute; trị t&agrave;i sản r&ograve;ng của c&aacute;c c&aacute; nh&acirc;n v&agrave; được cập nhật thường ni&ecirc;n. Về lĩnh vực người nổi tiếng, h&atilde;ng tin<em>&nbsp;</em>lấy số liệu từ c&aacute;c c&ocirc;ng ty đo lường như Nielsen, ComScore, IMDb v&agrave; c&aacute;c chuy&ecirc;n gia đầu ng&agrave;nh giải tr&iacute;.</p>\r\n<p class=\"Normal\"><strong>George Lucas</strong></p>\r\n<p class=\"Normal\">Đạo diễn&nbsp;<em>Star Wars</em>&nbsp;George Lucas, 79 tuổi, đứng đầu danh s&aacute;ch người nổi tiếng với khối t&agrave;i sản trị gi&aacute; 5,5 tỷ USD. Theo&nbsp;<em>Forbes</em>, phần lớn tiền &ocirc;ng kiếm được từ thương vụ b&aacute;n c&ocirc;ng ty sản xuất phim LucasFilm cho Disney v&agrave;o năm 2012. Disney trả &ocirc;ng hơn bốn tỷ USD gồm tiền mặt v&agrave; cổ phần. George Lucas trở th&agrave;nh tỷ ph&uacute; lần đầu ti&ecirc;n v&agrave;o năm 1997. Ảnh:&nbsp;<em>Reuters</em></p>\r\n</div>\r\n</div>\r\n<div class=\"item_slide_show clearfix\">\r\n<div id=\"slide_show_33385991\" class=\"block_thumb_slide_show\" data-thumbnail-src=\"https://i1-giaitri.vnecdn.net/2024/04/03/steven-spielberg-1712117973-1712119641-1712120703.jpg?w=220&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=qhg9_knUdjv3uAAf7I2i8g\" data-src=\"https://i1-giaitri.vnecdn.net/2024/04/03/steven-spielberg-1712117973-1712119641-1712120703.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=xlZoGNo1pXDrJSQcKJPXEg\" data-size-image=\"true\">\r\n<div class=\"block_thumb_picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2024/04/03/steven-spielberg-1712117973-1712119641-1712120703.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=KJW38W8dJUObpQRb2o8CSg 1x, https://i1-giaitri.vnecdn.net/2024/04/03/steven-spielberg-1712117973-1712119641-1712120703.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=8P5WEh37wGYD1153Oy1RmA 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2024/04/03/steven-spielberg-1712117973-1712119641-1712120703.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=KJW38W8dJUObpQRb2o8CSg 1x, https://i1-giaitri.vnecdn.net/2024/04/03/steven-spielberg-1712117973-1712119641-1712120703.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=8P5WEh37wGYD1153Oy1RmA 2x\"><img class=\"lazy lazy-blur lazy-unblur lazied\" src=\"https://i1-giaitri.vnecdn.net/2024/04/03/steven-spielberg-1712117973-1712119641-1712120703.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=KJW38W8dJUObpQRb2o8CSg\" alt=\"\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2024/04/03/steven-spielberg-1712117973-1712119641-1712120703.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=KJW38W8dJUObpQRb2o8CSg\" data-ll-status=\"loaded\"></picture></div>\r\n</div>\r\n<div class=\"desc_cation\">\r\n<p class=\"Normal\">Steven Spielberg, 77 tuổi, đạo diễn c&aacute;c bộ phim ăn kh&aacute;ch&nbsp;<em>Jaws&nbsp;</em>(1975),&nbsp;<em>Jurassic Park</em>&nbsp;(1993) v&agrave;&nbsp;<em>Schindler&rsquo;s List&nbsp;</em>(1993), đứng thứ hai với t&agrave;i sản 4,8 tỷ USD.</p>\r\n<p class=\"Normal\">Spielberg n&oacute;i với&nbsp;<em>Forbes&nbsp;</em>bản th&acirc;n kh&ocirc;ng nhận lương gần chục năm. Thay v&agrave;o đ&oacute;, &ocirc;ng nhận phần trăm tổng doanh thu từ c&aacute;c bộ phim của m&igrave;nh. Ngo&agrave;i ra, đạo diễn c&ograve;n nhận tiền hoa hồng từ mỗi v&eacute; tham quan c&ocirc;ng ty giải tr&iacute; Universal nhờ th&agrave;nh c&ocirc;ng của loạt phim&nbsp;<em>Indiana Jones</em>. Steven Spielberg l&agrave; đạo diễn đầu ti&ecirc;n g&oacute;p mặt trong danh s&aacute;ch tỷ ph&uacute; từ năm 1994. Ảnh:&nbsp;<em>Oscars</em></p>\r\n</div>\r\n</div>\r\n<div class=\"item_slide_show clearfix\">\r\n<div id=\"slide_show_33385992\" class=\"block_thumb_slide_show\" data-thumbnail-src=\"https://i1-giaitri.vnecdn.net/2024/04/03/231130092459-oprah-winfrey-1027-1712118089-1712119654-1712120703.jpg?w=220&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Omb_J28fEmBp3hgkOq56rQ\" data-src=\"https://i1-giaitri.vnecdn.net/2024/04/03/231130092459-oprah-winfrey-1027-1712118089-1712119654-1712120703.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=45gKQVJN2_0Xqi4AkAGIjQ\" data-size-image=\"true\">\r\n<div class=\"block_thumb_picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2024/04/03/231130092459-oprah-winfrey-1027-1712118089-1712119654-1712120703.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=yHZBrvbkk2CXB7WNSKIhyA 1x, https://i1-giaitri.vnecdn.net/2024/04/03/231130092459-oprah-winfrey-1027-1712118089-1712119654-1712120703.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=aTwbRkq5wUAuyUm9S4ZYeA 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2024/04/03/231130092459-oprah-winfrey-1027-1712118089-1712119654-1712120703.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=yHZBrvbkk2CXB7WNSKIhyA 1x, https://i1-giaitri.vnecdn.net/2024/04/03/231130092459-oprah-winfrey-1027-1712118089-1712119654-1712120703.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=aTwbRkq5wUAuyUm9S4ZYeA 2x\"><img class=\"lazy lazy-blur lazy-unblur lazied\" src=\"https://i1-giaitri.vnecdn.net/2024/04/03/231130092459-oprah-winfrey-1027-1712118089-1712119654-1712120703.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=yHZBrvbkk2CXB7WNSKIhyA\" alt=\"\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2024/04/03/231130092459-oprah-winfrey-1027-1712118089-1712119654-1712120703.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=yHZBrvbkk2CXB7WNSKIhyA\" data-ll-status=\"loaded\"></picture></div>\r\n</div>\r\n<div class=\"desc_cation\">\r\n<p class=\"Normal\">Người dẫn chương tr&igrave;nh ki&ecirc;m diễn vi&ecirc;n&nbsp;<a href=\"https://vnexpress.net/chu-de/oprah-winfrey-5129\" target=\"_blank\" rel=\"nofollow noopener\" data-itm-source=\"#vn_source=Detail-GiaiTri_GioiSao_QuocTe-4729991&amp;vn_campaign=Box-InternalLink&amp;vn_medium=Link-OprahWinfrey&amp;vn_term=Desktop&amp;vn_thumb=0\" data-itm-added=\"1\">Oprah Winfrey</a>, 70 tuổi, đứng thứ tư trong danh s&aacute;ch với tổng t&agrave;i sản 2,8 tỷ USD, sau huyền thoại b&oacute;ng rổ Michael Jordan.</p>\r\n<p class=\"Normal\">Thu nhập của b&agrave; kiếm được nhờ x&acirc;y dựng đế chế truyền th&ocirc;ng, điều h&agrave;nh c&ocirc;ng ty giải tr&iacute; v&agrave; kinh doanh bất động sản. B&agrave; l&agrave; phụ nữ da đen đầu ti&ecirc;n xuất hiện trong danh s&aacute;ch tỷ ph&uacute; của&nbsp;<em>Forbes</em>&nbsp;từ năm 2003. Ảnh:&nbsp;<em>Reuters</em></p>\r\n</div>\r\n</div>\r\n<div class=\"item_slide_show clearfix\">\r\n<div id=\"slide_show_33385993\" class=\"block_thumb_slide_show\" data-thumbnail-src=\"https://i1-giaitri.vnecdn.net/2024/04/03/hl32fm5bzvkzfhlef7k5vf3sjm-1712118467-1712119662-1712120703.jpg?w=220&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=42CyHviRhH4Vh4KZ4mI5zQ\" data-src=\"https://i1-giaitri.vnecdn.net/2024/04/03/hl32fm5bzvkzfhlef7k5vf3sjm-1712118467-1712119662-1712120703.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=NhCF_lp3gCq0CSmhzmPRWw\" data-size-image=\"true\">\r\n<div class=\"block_thumb_picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2024/04/03/hl32fm5bzvkzfhlef7k5vf3sjm-1712118467-1712119662-1712120703.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=_UBd7KBtzzfBfW59Gr6agw 1x, https://i1-giaitri.vnecdn.net/2024/04/03/hl32fm5bzvkzfhlef7k5vf3sjm-1712118467-1712119662-1712120703.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=mUvkkXhTlwOFUK4BNArjNA 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2024/04/03/hl32fm5bzvkzfhlef7k5vf3sjm-1712118467-1712119662-1712120703.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=_UBd7KBtzzfBfW59Gr6agw 1x, https://i1-giaitri.vnecdn.net/2024/04/03/hl32fm5bzvkzfhlef7k5vf3sjm-1712118467-1712119662-1712120703.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=mUvkkXhTlwOFUK4BNArjNA 2x\"><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2024/04/03/hl32fm5bzvkzfhlef7k5vf3sjm-1712118467-1712119662-1712120703.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=_UBd7KBtzzfBfW59Gr6agw\" alt=\"\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2024/04/03/hl32fm5bzvkzfhlef7k5vf3sjm-1712118467-1712119662-1712120703.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=_UBd7KBtzzfBfW59Gr6agw\" data-ll-status=\"loaded\"></picture></div>\r\n</div>\r\n<div class=\"desc_cation\">\r\n<p class=\"Normal\">Rapper&nbsp;<a href=\"https://vnexpress.net/chu-de/jay-z-5406\" target=\"_blank\" rel=\"nofollow noopener\" data-itm-source=\"#vn_source=Detail-GiaiTri_GioiSao_QuocTe-4729991&amp;vn_campaign=Box-InternalLink&amp;vn_medium=Link-JayZ&amp;vn_term=Desktop&amp;vn_thumb=0\" data-itm-added=\"1\">Jay-Z</a>, 54 tuổi, chồng của ca sĩ Beyonc&eacute; xếp thứ năm. Khối t&agrave;i sản 2,5 tỷ USD kiếm được từ việc b&aacute;n 50% cổ phần thương hiệu rượu s&acirc;m banh cho \"g&atilde; khổng lồ h&agrave;ng xa xỉ\" LVMH v&agrave;o năm 2021, thu &iacute;t nhất 300 triệu USD. Ngo&agrave;i ra, rapper c&ograve;n b&aacute;n phần lớn cổ phần nh&atilde;n hiệu rượu kh&aacute;c v&agrave;o năm 2023, trị gi&aacute; 750 triệu USD. Nghệ sĩ cũng l&agrave; cổ đ&ocirc;ng của Uber v&agrave; Block. Theo&nbsp;<em>Forbes</em>, t&agrave;i sản r&ograve;ng của Jay-Z kh&ocirc;ng bao gồm sản nghiệp nửa tỷ USD của Beyonc&eacute;. Ảnh:&nbsp;<em>Reuters</em></p>\r\n</div>\r\n</div>\r\n<div class=\"item_slide_show clearfix\">\r\n<div id=\"slide_show_33385994\" class=\"block_thumb_slide_show\" data-thumbnail-src=\"https://i1-giaitri.vnecdn.net/2024/04/03/433100456-18612957565060659-3273-1712118747-1712119685-1712120704.jpg?w=220&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=SG-kFdMosSW8CuCujMuJ7A\" data-src=\"https://i1-giaitri.vnecdn.net/2024/04/03/433100456-18612957565060659-3273-1712118747-1712119685-1712120704.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=EKJ6LNFyDNVVJWF6-boVmg\" data-size-image=\"true\">\r\n<div class=\"block_thumb_picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2024/04/03/433100456-18612957565060659-3273-1712118747-1712119685-1712120704.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7S7236pD2OqZcR4lt9yW-Q 1x, https://i1-giaitri.vnecdn.net/2024/04/03/433100456-18612957565060659-3273-1712118747-1712119685-1712120704.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=zfmoWv1SdD6j1J6LJ2kSkw 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2024/04/03/433100456-18612957565060659-3273-1712118747-1712119685-1712120704.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7S7236pD2OqZcR4lt9yW-Q 1x, https://i1-giaitri.vnecdn.net/2024/04/03/433100456-18612957565060659-3273-1712118747-1712119685-1712120704.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=zfmoWv1SdD6j1J6LJ2kSkw 2x\"><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2024/04/03/433100456-18612957565060659-3273-1712118747-1712119685-1712120704.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7S7236pD2OqZcR4lt9yW-Q\" alt=\"\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2024/04/03/433100456-18612957565060659-3273-1712118747-1712119685-1712120704.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7S7236pD2OqZcR4lt9yW-Q\" data-ll-status=\"loaded\"></picture></div>\r\n</div>\r\n<div class=\"desc_cation\">\r\n<p class=\"Normal\"><strong>Kim Kardashian</strong></p>\r\n<p class=\"Normal\">Ng&ocirc;i sao chương tr&igrave;nh truyền h&igrave;nh&nbsp;<a href=\"https://vnexpress.net/chu-de/kim-kardashian-3785\" target=\"_blank\" rel=\"nofollow noopener\" data-itm-source=\"#vn_source=Detail-GiaiTri_GioiSao_QuocTe-4729991&amp;vn_campaign=Box-InternalLink&amp;vn_medium=Link-KimKardashian&amp;vn_term=Desktop&amp;vn_thumb=0\" data-itm-added=\"1\">Kim Kardashian</a>, 43 tuổi, đứng vị tr&iacute; thứ s&aacute;u với khối t&agrave;i sản r&ograve;ng 1,7 tỷ USD. C&ocirc; trở th&agrave;nh tỷ ph&uacute; nhờ kinh doanh thời trang v&agrave; mỹ phẩm. Năm 2023, d&ograve;ng đồ l&oacute;t định h&igrave;nh thuộc h&atilde;ng SKIMS của c&ocirc; được&nbsp;<em>Forbes&nbsp;</em>định gi&aacute; 4 tỷ USD trong v&ograve;ng cấp vốn. Trước đ&oacute;, Kim b&aacute;n 20% cổ phần thương hiệu chăm s&oacute;c da KKW Beauty cho tập đo&agrave;n Coty với 200 triệu USD năm 2020 rồi đ&oacute;ng cửa c&ocirc;ng ty. Ản:<em>&nbsp;Instagram Kim Kardashian</em></p>\r\n</div>\r\n</div>\r\n<div class=\"item_slide_show clearfix\">\r\n<div id=\"slide_show_33385995\" class=\"block_thumb_slide_show\" data-thumbnail-src=\"https://i1-giaitri.vnecdn.net/2024/04/03/mv5byjfjothjmjgtyzm5zs00yjc0ltk5-1712118843-1712119696-1712120704.jpg?w=220&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=cW_RUzKTvysfidFVBKcXpA\" data-src=\"https://i1-giaitri.vnecdn.net/2024/04/03/mv5byjfjothjmjgtyzm5zs00yjc0ltk5-1712118843-1712119696-1712120704.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=27aD9LI_brGulhTcio0dkQ\" data-size-image=\"true\">\r\n<div class=\"block_thumb_picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2024/04/03/mv5byjfjothjmjgtyzm5zs00yjc0ltk5-1712118843-1712119696-1712120704.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2zSfqfznXJignrHqpfzFow 1x, https://i1-giaitri.vnecdn.net/2024/04/03/mv5byjfjothjmjgtyzm5zs00yjc0ltk5-1712118843-1712119696-1712120704.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=ABBqRKemt-S8Gnd8Dk5RZA 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2024/04/03/mv5byjfjothjmjgtyzm5zs00yjc0ltk5-1712118843-1712119696-1712120704.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2zSfqfznXJignrHqpfzFow 1x, https://i1-giaitri.vnecdn.net/2024/04/03/mv5byjfjothjmjgtyzm5zs00yjc0ltk5-1712118843-1712119696-1712120704.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=ABBqRKemt-S8Gnd8Dk5RZA 2x\"><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2024/04/03/mv5byjfjothjmjgtyzm5zs00yjc0ltk5-1712118843-1712119696-1712120704.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2zSfqfznXJignrHqpfzFow\" alt=\"\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2024/04/03/mv5byjfjothjmjgtyzm5zs00yjc0ltk5-1712118843-1712119696-1712120704.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2zSfqfznXJignrHqpfzFow\" data-ll-status=\"loaded\"></picture></div>\r\n</div>\r\n<div class=\"desc_cation\">\r\n<p class=\"Normal\"><strong>Peter Jackson</strong></p>\r\n<p class=\"Normal\">Hạng bảy thuộc về Peter Jackson, 62 tuổi, gi&aacute;m đốc h&igrave;nh ảnh New Zealand. &Ocirc;ng l&agrave; người đứng sau th&agrave;nh c&ocirc;ng của hai bom tấn thương mại<em>&nbsp;Lord of the Rings</em>&nbsp;v&agrave;&nbsp;<em>The Hobbit</em>.</p>\r\n<p class=\"Normal\"><em>Forbes&nbsp;</em>cho biết phần lớn t&agrave;i sản 1,5 tỷ USD của Jackson kiếm từ việc b&aacute;n một phần cổ phiếu của c&ocirc;ng ty hiệu ứng Weta Digital cho nh&agrave; sản xuất game Unity Software v&agrave;o năm 2021. Thương vụ đ&oacute; mang lại cho &ocirc;ng gần 1 tỷ USD tiền mặt v&agrave; cổ phần. Ảnh:&nbsp;<em>IMDb</em></p>\r\n</div>\r\n</div>\r\n<div class=\"item_slide_show clearfix\">\r\n<div id=\"slide_show_33385996\" class=\"block_thumb_slide_show\" data-thumbnail-src=\"https://i1-